mapper.select:
SELECT example.id as "id", example.cover_url as "coverUrl", example.NAME as "name", example.describes as "describes", example.keyword as "keyword", example.create_time as "createTime", example.priceName, example.houseTypeName, example.styleName, u.id as "userId", u.USER_NAME as "userName", u.nick_name as "nickName", u.USER_HEAD_URL as "userHeadUrl" FROM ( SELECT t2.*, dic3.TYPE_NAME AS "priceName" FROM ( SELECT t1.*, dic2.TYPE_NAME AS "houseTypeName" FROM ( SELECT d.*, dic.TYPE_NAME AS "styleName" FROM ddn_example d LEFT JOIN base_dictionaries dic ON d.STYLE_ID = dic.id ) AS t1 LEFT JOIN base_dictionaries dic2 ON t1.house_type_id = dic2.id ) t2 LEFT JOIN base_dictionaries dic3 ON t2.price_id = dic3.id ) example LEFT JOIN base_user u ON example.create_user_id = u.id
AND example.price_id = #{search.priceId}
AND example.style_id = #{search.styleId}
AND example.house_type_id = #{search.houseTypeId}
AND example.name LIKE '%${search.name}%'
AND example.keyword LIKE '%${search.keyword}%'
order by example.create_time desc
